						SELECT @priceProduct_ = [price_product] FROM [WhiskyClubCentral].[dbo].[Product] WHERE [id_product] = @idProduct_
						--SUBTOTAL
						SET @subtotal_ = (@priceProduct_ * @amountProduct_) * @moneyEquivalence_cliente

						--GENERATE LINE OF FACT
